import { useState, useEffect, useRef } from 'react';
import { UserRole } from '@/types';
import { cn } from '@/lib/utils';
import { GraduationCap, Projector, ShieldCheck } from 'lucide-react';

interface RoleSelectorProps {
    selectedRole: UserRole;
    onSelect: (role: UserRole) => void;
}

export function RoleSelector({ selectedRole, onSelect }: RoleSelectorProps) {
    const [activeIndex, setActiveIndex] = useState(0);
    const [hoveredRole, setHoveredRole] = useState<UserRole | null>(null);
    const [mousePos, setMousePos] = useState({ x: 0, y: 0 });
    const [isHoveringContainer, setIsHoveringContainer] = useState(false);
    const containerRef = useRef<HTMLDivElement>(null);

    const roles: { id: UserRole; label: string; icon: any }[] = [
        { id: 'learner', label: 'Learner', icon: GraduationCap },
        { id: 'instructor', label: 'Instructor', icon: Projector },
        { id: 'admin', label: 'Admin', icon: ShieldCheck },
    ];

    useEffect(() => {
        const index = roles.findIndex((r) => r.id === selectedRole);
        setActiveIndex(index >= 0 ? index : 0);
    }, [selectedRole]);

    const handleMouseMove = (e: React.MouseEvent<HTMLDivElement>) => {
        if (!containerRef.current) return;
        const rect = containerRef.current.getBoundingClientRect();
        setMousePos({ x: e.clientX - rect.left, y: e.clientY - rect.top });
        setIsHoveringContainer(true);
    };

    const handleMouseLeave = () => {
        setIsHoveringContainer(false);
        setHoveredRole(null);
    };

    return (
        <div className="w-full max-w-md mx-auto mb-6 animate-fade-in group perspective-1000">
            {/* Main Interactive Container */}
            <div
                ref={containerRef}
                onMouseMove={handleMouseMove}
                onMouseLeave={handleMouseLeave}
                className="relative grid grid-cols-3 gap-1 p-1.5 bg-muted/20 backdrop-blur-xl border border-white/10 rounded-2xl overflow-hidden transition-all duration-500 hover:shadow-[0_0_25px_rgba(113,75,110,0.15)] ring-1 ring-white/5"
            >
                {/* 1. Dynamic Mouse-Follow Glow (Container Level) */}
                <div
                    className="absolute inset-0 pointer-events-none transition-opacity duration-500 wil-change-[background]"
                    style={{
                        opacity: isHoveringContainer ? 1 : 0,
                        background: `radial-gradient(600px circle at ${mousePos.x}px ${mousePos.y}px, rgba(113, 75, 110, 0.15), transparent 40%)`
                    }}
                />

                {/* 2. Animated Sliding Background Pill (Selection) */}
                <div
                    className="absolute top-1.5 bottom-1.5 rounded-xl bg-background/80 shadow-[0_2px_15px_-3px_rgba(0,0,0,0.1)] border border-border/50 transition-all duration-500 cubic-bezier(0.34, 1.56, 0.64, 1)"
                    style={{
                        left: `calc(6px + ${activeIndex} * ((100% - 12px) / 3))`,
                        width: `calc((100% - 12px) / 3)`,
                    }}
                >
                    {/* Inner Pill Glow Trail */}
                    <div className="absolute inset-0 rounded-xl bg-gradient-to-br from-primary/10 via-transparent to-accent/5 opacity-100" />
                    {/* Active Bottom Highlight */}
                    <div className="absolute -bottom-[1px] left-3 right-3 h-[2px] bg-gradient-to-r from-transparent via-primary/60 to-transparent blur-[1px]" />
                </div>

                {/* Role Options */}
                {roles.map((role) => {
                    const isActive = selectedRole === role.id;
                    const isHovered = hoveredRole === role.id;
                    const Icon = role.icon;
                    // Fade out non-hovered items slightly when interacting
                    const isDimmed = isHoveringContainer && !isHovered && !isActive;

                    return (
                        <button
                            key={role.id}
                            onClick={() => onSelect(role.id)}
                            onMouseEnter={() => setHoveredRole(role.id)}
                            className={cn(
                                "relative z-10 flex-1 flex flex-col items-center justify-center py-3 px-2 text-sm font-medium transition-all duration-300 select-none outline-none rounded-xl",
                                "focus-visible:ring-2 focus-visible:ring-primary/50",
                                isActive
                                    ? "text-primary scale-105 cursor-default"
                                    : "text-muted-foreground cursor-pointer hover:text-foreground",
                                isDimmed && "opacity-50 blur-[0.5px] scale-95",
                                isHovered && !isActive && "scale-105 bg-white/5"
                            )}
                        >
                            {/* Icon Container */}
                            <div className={cn(
                                "mb-1.5 p-1.5 rounded-full transition-all duration-500 relative",
                                isActive ? "bg-primary/10 text-primary -translate-y-0.5" : "bg-transparent",
                                isHovered && !isActive && "bg-white/10 text-foreground"
                            )}>
                                <Icon className={cn(
                                    "h-5 w-5 transition-all duration-300",
                                    isActive && "drop-shadow-[0_0_10px_rgba(113,75,110,0.6)]",
                                    isHovered && "animate-pulse"
                                )} />

                                {/* Orbiting Dot for Active State */}
                                {isActive && (
                                    <span className="absolute -top-0.5 -right-0.5 flex h-2.5 w-2.5">
                                        <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-primary/40 opacity-75"></span>
                                        <span className="relative inline-flex rounded-full h-2.5 w-2.5 bg-primary"></span>
                                    </span>
                                )}
                            </div>

                            {/* Label */}
                            <span className={cn(
                                "tracking-tight transition-all duration-300",
                                isActive ? "font-bold text-foreground" : "font-medium",
                                isHovered && "tracking-wide"
                            )}>
                                {role.label}
                            </span>
                        </button>
                    );
                })}
            </div>

            {/* Helper text with smooth height transition */}
            <div className="mt-4 text-center h-6 overflow-hidden">
                <p key={selectedRole} className="text-xs text-muted-foreground animate-slide-up font-medium">
                    {selectedRole === 'learner' && "üéì Access courses, track progress, and earn certificates."}
                    {selectedRole === 'instructor' && "üìΩÔ∏è Create courses, manage students, and view reports."}
                    {selectedRole === 'admin' && "üõ°Ô∏è System configuration, user management, and oversight."}
                </p>
            </div>
        </div>
    );
}

import {
    Dialog,
    DialogContent,
    DialogHeader,
    DialogTitle,
    DialogFooter,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/types";
import { Trophy, Star } from "lucide-react";
import { useEffect, useState } from "react";
import confetti from "canvas-confetti";

interface GamificationPopupProps {
    isOpen: boolean;
    onClose: () => void;
    pointsEarned: number;
    currentBadge: Badge | null;
    nextBadge: Badge | null;
    progress: number;
}

export function GamificationPopup({
    isOpen,
    onClose,
    pointsEarned,
    currentBadge,
    nextBadge,
    progress,
    title = "Congratulations!",
    buttonText = "Awesome!",
}: GamificationPopupProps & { title?: string; buttonText?: string }) {

    useEffect(() => {
        if (isOpen) {
            const duration = 3000;
            const animationEnd = Date.now() + duration;
            const defaults = { startVelocity: 30, spread: 360, ticks: 60, zIndex: 0 };

            const random = (min: number, max: number) => {
                return Math.random() * (max - min) + min;
            }

            const interval: any = setInterval(function () {
                const timeLeft = animationEnd - Date.now();

                if (timeLeft <= 0) {
                    return clearInterval(interval);
                }

                const particleCount = 50 * (timeLeft / duration);
                // since particles fall down, start a bit higher than random
                confetti({ ...defaults, particleCount, origin: { x: random(0.1, 0.3), y: Math.random() - 0.2 } });
                confetti({ ...defaults, particleCount, origin: { x: random(0.7, 0.9), y: Math.random() - 0.2 } });
            }, 250);

            return () => clearInterval(interval);
        }
    }, [isOpen]);

    return (
        <Dialog open={isOpen} onOpenChange={(open) => !open && onClose()}>
            <DialogContent className="sm:max-w-md text-center">
                <DialogHeader>
                    <div className="mx-auto mb-4 flex h-20 w-20 items-center justify-center rounded-full bg-yellow-100">
                        <Trophy className="h-10 w-10 text-yellow-600" />
                    </div>
                    <DialogTitle className="text-2xl font-bold text-center">
                        {title}
                    </DialogTitle>
                </DialogHeader>

                <div className="py-6">
                    <p className="mb-2 text-lg font-medium text-muted-foreground">
                        You have earned
                    </p>
                    <div className="mb-8 flex items-center justify-center gap-2">
                        <Star className="h-8 w-8 fill-yellow-400 text-yellow-400" />
                        <span className="text-4xl font-bold text-primary">{pointsEarned} Points</span>
                    </div>

                    <div className="space-y-2 rounded-lg bg-muted/30 p-4">
                        <div className="flex items-center justify-between text-sm">
                            <span className="font-medium">Progress to {nextBadge?.name || 'Max Rank'}</span>
                            <span className="text-muted-foreground">{progress}%</span>
                        </div>
                        <Progress value={progress} className="h-3" />
                        <div className="flex justify-between text-xs text-muted-foreground">
                            <span>{currentBadge?.name}</span>
                            <span>{nextBadge?.name}</span>
                        </div>
                    </div>
                </div>

                <DialogFooter className="sm:justify-center">
                    <Button onClick={onClose} size="lg" className="w-full sm:w-auto">
                        {buttonText}
                    </Button>
                </DialogFooter>
            </DialogContent>
        </Dialog>
    );
}

import { Link, useLocation, useNavigate } from 'react-router-dom';
import { GraduationCap, BookOpen, LayoutDashboard, Menu, X, User, LogOut, Settings, ArrowLeft } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu';
import { useAuth } from '@/contexts/AuthContext';
import { useState } from 'react';
import { cn } from '@/lib/utils';

export function LearnerNavbar() {
  const { user, isAuthenticated, logout } = useAuth();
  const location = useLocation();
  const navigate = useNavigate();
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  const navLinks: { href: string; label: string; icon: any }[] = [];

  const isActive = (path: string) => location.pathname === path;

  return (
    <header className="sticky top-0 z-50 w-full">
      {/* Mirror/Glassmorphism Base Layer */}
      <div className="absolute inset-0 bg-background/60 backdrop-blur-2xl supports-[backdrop-filter]:bg-background/40" />

      {/* Glossy Reflective Shine Effect */}
      <div className="absolute inset-0 bg-gradient-to-b from-white/10 via-transparent to-transparent pointer-events-none opacity-50 overflow-hidden">
        <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/5 to-transparent animate-mirror-shine w-1/2" />
      </div>

      {/* Classy Bottom Border Beam */}
      <div className="absolute bottom-0 left-0 right-0 h-[1px] bg-gradient-to-r from-transparent via-border/50 to-transparent" />

      {/* Ultra-subtle inner glow */}
      <div className="absolute inset-0 shadow-[inset_0_1px_1px_rgba(255,255,255,0.05)] pointer-events-none" />

      <div className="container relative z-10 flex h-16 items-center justify-between">
        {/* Logo */}
        <Link
          to={isAuthenticated ? "/my-courses" : "/"}
          className="flex items-center gap-3 transition-transform hover:scale-[1.02] active:scale-95 group"
        >
          <div className="relative flex h-10 w-10 items-center justify-center overflow-hidden rounded-xl bg-white/5 shadow-2xl transition-all group-hover:bg-white/10 group-hover:shadow-primary/20">
            <img
              src="/logo.png"
              alt="LearnSphere Logo"
              className="h-full w-full object-cover mix-blend-normal transition-all duration-500 group-hover:scale-110 drop-shadow-[0_0_12px_rgba(113,75,110,0.6)]"
            />
            {/* Subtle glow orb behind logo */}
            <div className="absolute inset-0 bg-primary/20 blur-xl opacity-0 group-hover:opacity-100 transition-opacity duration-500" />
          </div>
          <span className="font-display text-2xl font-extrabold tracking-tight text-foreground transition-colors group-hover:text-primary">
            LearnSphere
          </span>
        </Link>

        {/* Desktop Navigation */}
        <nav className="hidden items-center gap-6 md:flex">
          {navLinks.map(({ href, label }) => (
            <Link
              key={href}
              to={href}
              className={cn(
                "text-sm font-medium transition-colors hover:text-primary",
                isActive(href) ? "text-primary" : "text-muted-foreground"
              )}
            >
              {label}
            </Link>
          ))}
        </nav>

        {/* Desktop Auth */}
        <div className="hidden items-center gap-3 md:flex">
          {isAuthenticated ? (
            <>
              {(user?.role === 'admin' || user?.role === 'instructor') && (
                <Button variant="outline" size="sm" asChild>
                  <Link to="/backoffice">
                    <LayoutDashboard className="mr-2 h-4 w-4" />
                    Backoffice
                  </Link>
                </Button>
              )}
              <DropdownMenu>
                <DropdownMenuTrigger asChild>
                  <Button variant="ghost" className="relative h-9 w-9 rounded-full">
                    <Avatar className="h-9 w-9">
                      <AvatarImage src={user?.avatar} alt={user?.name} />
                      <AvatarFallback className="gradient-primary text-primary-foreground">
                        {user?.name?.charAt(0).toUpperCase()}
                      </AvatarFallback>
                    </Avatar>
                  </Button>
                </DropdownMenuTrigger>
                <DropdownMenuContent className="w-56" align="end" forceMount>
                  <div className="flex items-center justify-start gap-2 p-2">
                    <div className="flex flex-col space-y-1 leading-none">
                      <p className="font-medium">{user?.name}</p>
                      <p className="text-xs text-muted-foreground">{user?.email}</p>
                    </div>
                  </div>
                  <DropdownMenuSeparator />
                  <DropdownMenuItem asChild>
                    <Link
                      to="/courses"
                      className={cn(
                        "w-full cursor-pointer",
                        isActive('/courses') ? "bg-primary/10 text-primary" : "text-muted-foreground"
                      )}
                    >
                      <BookOpen className="mr-2 h-4 w-4" />
                      Courses
                    </Link>
                  </DropdownMenuItem>
                  <DropdownMenuItem asChild>
                    <Link
                      to="/my-courses"
                      className={cn(
                        "w-full cursor-pointer",
                        isActive('/my-courses') ? "bg-primary/10 text-primary" : "text-muted-foreground"
                      )}
                    >
                      <GraduationCap className="mr-2 h-4 w-4" />
                      My Courses
                    </Link>
                  </DropdownMenuItem>
                  <DropdownMenuItem asChild>
                    <Link
                      to="/settings"
                      className={cn(
                        "w-full cursor-pointer",
                        isActive('/settings') ? "bg-primary/10 text-primary" : "text-muted-foreground"
                      )}
                    >
                      <Settings className="mr-2 h-4 w-4" />
                      Settings
                    </Link>
                  </DropdownMenuItem>
                  <DropdownMenuSeparator />
                  <DropdownMenuItem onClick={logout} className="text-destructive">
                    <LogOut className="mr-2 h-4 w-4" />
                    Log out
                  </DropdownMenuItem>
                </DropdownMenuContent>
              </DropdownMenu>
            </>
          ) : (
            <>
              <Button variant="ghost" size="sm" asChild>
                <Link to="/login">Sign In</Link>
              </Button>
              <Button size="sm" asChild>
                <Link to="/register">Get Started</Link>
              </Button>
            </>
          )}
        </div>

        {/* Mobile Menu Button */}
        <Button
          variant="ghost"
          size="icon"
          className="md:hidden"
          onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
        >
          {mobileMenuOpen ? <X className="h-5 w-5" /> : <Menu className="h-5 w-5" />}
        </Button>
      </div>

      {/* Mobile Menu */}
      {mobileMenuOpen && (
        <div className="border-t border-border md:hidden">
          <nav className="container flex flex-col gap-2 py-4">
            <Link
              to="/courses"
              onClick={() => setMobileMenuOpen(false)}
              className={cn(
                "flex items-center gap-2 rounded-lg px-3 py-2 text-sm font-medium transition-colors",
                isActive('/courses') ? "bg-primary/10 text-primary" : "text-muted-foreground hover:bg-muted"
              )}
            >
              <BookOpen className="h-4 w-4" />
              Courses
            </Link>
            {isAuthenticated && (
              <Link
                to="/my-courses"
                onClick={() => setMobileMenuOpen(false)}
                className={cn(
                  "flex items-center gap-2 rounded-lg px-3 py-2 text-sm font-medium transition-colors",
                  isActive('/my-courses') ? "bg-primary/10 text-primary" : "text-muted-foreground hover:bg-muted"
                )}
              >
                <GraduationCap className="h-4 w-4" />
                My Courses
              </Link>
            )}
            <div className="my-2 h-px bg-border" />
            {isAuthenticated ? (
              <>
                {(user?.role === 'admin' || user?.role === 'instructor') && (
                  <Link
                    to="/backoffice"
                    onClick={() => setMobileMenuOpen(false)}
                    className="flex items-center gap-2 rounded-lg px-3 py-2 text-sm font-medium text-muted-foreground hover:bg-muted"
                  >
                    <LayoutDashboard className="h-4 w-4" />
                    Backoffice
                  </Link>
                )}
                <button
                  onClick={() => {
                    logout();
                    setMobileMenuOpen(false);
                  }}
                  className="flex items-center gap-2 rounded-lg px-3 py-2 text-sm font-medium text-destructive hover:bg-destructive/10"
                >
                  <LogOut className="h-4 w-4" />
                  Log out
                </button>
              </>
            ) : (
              <div className="flex gap-2">
                <Button variant="outline" className="flex-1" asChild>
                  <Link to="/login" onClick={() => setMobileMenuOpen(false)}>Sign In</Link>
                </Button>
                <Button className="flex-1" asChild>
                  <Link to="/register" onClick={() => setMobileMenuOpen(false)}>Get Started</Link>
                </Button>
              </div>
            )}
          </nav>
        </div>
      )}
    </header>
  );
}

import * as React from "react";
import { cva, type VariantProps } from "class-variance-authority";

import { cn } from "@/lib/utils";

const badgeVariants = cva(
  "inline-flex items-center rounded-full border px-2.5 py-0.5 text-xs font-semibold transition-colors focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2",
  {
    variants: {
      variant: {
        default: "border-transparent bg-primary text-primary-foreground hover:bg-primary/80",
        secondary: "border-transparent bg-secondary text-secondary-foreground hover:bg-secondary/80",
        destructive: "border-transparent bg-destructive text-destructive-foreground hover:bg-destructive/80",
        outline: "text-foreground",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  },
);

export interface BadgeProps extends React.HTMLAttributes<HTMLDivElement>, VariantProps<typeof badgeVariants> {}

function Badge({ className, variant, ...props }: BadgeProps) {
  return <div className={cn(badgeVariants({ variant }), className)} {...props} />;
}

export { Badge, badgeVariants };

import * as React from "react";
import { Slot } from "@radix-ui/react-slot";
import { cva, type VariantProps } from "class-variance-authority";

import { cn } from "@/lib/utils";

const buttonVariants = cva(
  "inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium ring-offset-background transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg]:size-4 [&_svg]:shrink-0",
  {
    variants: {
      variant: {
        default: "bg-primary text-primary-foreground hover:bg-primary/90",
        destructive: "bg-destructive text-destructive-foreground hover:bg-destructive/90",
        outline: "border border-input bg-background hover:bg-accent hover:text-accent-foreground",
        secondary: "bg-secondary text-secondary-foreground hover:bg-secondary/80",
        ghost: "hover:bg-accent hover:text-accent-foreground",
        link: "text-primary underline-offset-4 hover:underline",
      },
      size: {
        default: "h-10 px-4 py-2",
        sm: "h-9 rounded-md px-3",
        lg: "h-11 rounded-md px-8",
        icon: "h-10 w-10",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  },
);

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean;
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, asChild = false, ...props }, ref) => {
    const Comp = asChild ? Slot : "button";
    return <Comp className={cn(buttonVariants({ variant, size, className }))} ref={ref} {...props} />;
  },
);
Button.displayName = "Button";

export { Button, buttonVariants };

import * as React from "react";
import { type DialogProps } from "@radix-ui/react-dialog";
import { Command as CommandPrimitive } from "cmdk";
import { Search } from "lucide-react";

import { cn } from "@/lib/utils";
import { Dialog, DialogContent } from "@/components/ui/dialog";

const Command = React.forwardRef<
  React.ElementRef<typeof CommandPrimitive>,
  React.ComponentPropsWithoutRef<typeof CommandPrimitive>
>(({ className, ...props }, ref) => (
  <CommandPrimitive
    ref={ref}
    className={cn(
      "flex h-full w-full flex-col overflow-hidden rounded-md bg-popover text-popover-foreground",
      className,
    )}
    {...props}
  />
));
Command.displayName = CommandPrimitive.displayName;

interface CommandDialogProps extends DialogProps {}

const CommandDialog = ({ children, ...props }: CommandDialogProps) => {
  return (
    <Dialog {...props}>
      <DialogContent className="overflow-hidden p-0 shadow-lg">
        <Command className="[&_[cmdk-group-heading]]:px-2 [&_[cmdk-group-heading]]:font-medium [&_[cmdk-group-heading]]:text-muted-foreground [&_[cmdk-group]:not([hidden])_~[cmdk-group]]:pt-0 [&_[cmdk-group]]:px-2 [&_[cmdk-input-wrapper]_svg]:h-5 [&_[cmdk-input-wrapper]_svg]:w-5 [&_[cmdk-input]]:h-12 [&_[cmdk-item]]:px-2 [&_[cmdk-item]]:py-3 [&_[cmdk-item]_svg]:h-5 [&_[cmdk-item]_svg]:w-5">
          {children}
        </Command>
      </DialogContent>
    </Dialog>
  );
};

const CommandInput = React.forwardRef<
  React.ElementRef<typeof CommandPrimitive.Input>,
  React.ComponentPropsWithoutRef<typeof CommandPrimitive.Input>
>(({ className, ...props }, ref) => (
  <div className="flex items-center border-b px-3" cmdk-input-wrapper="">
    <Search className="mr-2 h-4 w-4 shrink-0 opacity-50" />
    <CommandPrimitive.Input
      ref={ref}
      className={cn(
        "flex h-11 w-full rounded-md bg-transparent py-3 text-sm outline-none placeholder:text-muted-foreground disabled:cursor-not-allowed disabled:opacity-50",
        className,
      )}
      {...props}
    />
  </div>
));

CommandInput.displayName = CommandPrimitive.Input.displayName;

const CommandList = React.forwardRef<
  React.ElementRef<typeof CommandPrimitive.List>,
  React.ComponentPropsWithoutRef<typeof CommandPrimitive.List>
>(({ className, ...props }, ref) => (
  <CommandPrimitive.List
    ref={ref}
    className={cn("max-h-[300px] overflow-y-auto overflow-x-hidden", className)}
    {...props}
  />
));

CommandList.displayName = CommandPrimitive.List.displayName;

const CommandEmpty = React.forwardRef<
  React.ElementRef<typeof CommandPrimitive.Empty>,
  React.ComponentPropsWithoutRef<typeof CommandPrimitive.Empty>
>((props, ref) => <CommandPrimitive.Empty ref={ref} className="py-6 text-center text-sm" {...props} />);

CommandEmpty.displayName = CommandPrimitive.Empty.displayName;

const CommandGroup = React.forwardRef<
  React.ElementRef<typeof CommandPrimitive.Group>,
  React.ComponentPropsWithoutRef<typeof CommandPrimitive.Group>
>(({ className, ...props }, ref) => (
  <CommandPrimitive.Group
    ref={ref}
    className={cn(
