  const { user, addPoints, currentBadge, nextBadge, progressToNextBadge } = useAuth();
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [currentLessonIndex, setCurrentLessonIndex] = useState(0);
  const [completedLessons, setCompletedLessons] = useState<string[]>(['l1', 'l2']);
  const [showGamificationPopup, setShowGamificationPopup] = useState(false);
  const [pointsEarned, setPointsEarned] = useState(0);

  // Course completion state
  const [courseCompleted, setCourseCompleted] = useState(false);

  const course = useMemo(() => mockCourses.find((c) => c.id === courseId), [courseId]);
  const lessons = useMemo(() => mockLessons.filter((l) => l.courseId === courseId), [courseId]);
  const enrollment = useMemo(() => mockEnrollments.find(
    (e) => e.courseId === courseId && e.userId === user?.id
  ), [courseId, user?.id]);


  // Handle navigation to specific lesson from CourseDetailPage
  useEffect(() => {
    const lessonId = (location.state as any)?.lessonId;
    if (lessonId && lessons.length > 0) {
      const index = lessons.findIndex((l) => l.id === lessonId);
      if (index !== -1) {
        setCurrentLessonIndex(index);
        // On mobile, close sidebar when navigating to a specific lesson
        if (window.innerWidth < 1024) {
          setSidebarOpen(false);
        }
      }
    }
  }, [location.state, lessons]); // Added location.state to dependencies

  const currentLesson = lessons[currentLessonIndex];

  if (!course || !currentLesson) {
    return (
      <div className="flex min-h-screen items-center justify-center">
        <div className="text-center">
          <h1 className="mb-4 text-2xl font-bold">Content not found</h1>
          <p className="mb-4 text-muted-foreground">
            {lessons.length === 0 ? "This course has no content yet." : "Lesson not found."}
          </p>
          <Button asChild>
            <Link to="/courses">Back to Courses</Link>
          </Button>
        </div>
      </div>
    );
  }

  const progress = Math.round((completedLessons.length / lessons.length) * 100);

  const markComplete = () => {
    if (!completedLessons.includes(currentLesson.id)) {
      setCompletedLessons([...completedLessons, currentLesson.id]);
    }
  };

  const goToNext = () => {
    markComplete();
    if (currentLessonIndex < lessons.length - 1) {
      setCurrentLessonIndex(currentLessonIndex + 1);
      window.scrollTo(0, 0); // Scroll to top
    }
  };

  const handleCompleteCourse = () => {
    markComplete();
    setCourseCompleted(true);
    // Award points (mock amount)
    const points = 50;
    addPoints(points);
    setPointsEarned(points);
    setShowGamificationPopup(true);
  };

  const goToPrevious = () => {
    if (currentLessonIndex > 0) {
      setCurrentLessonIndex(currentLessonIndex - 1);
      window.scrollTo(0, 0); // Scroll to top
    }
  };

  const handleLessonSelect = (index: number) => {
    setCurrentLessonIndex(index);
    // Close sidebar on mobile when selection is made
    if (window.innerWidth < 1024) {
      setSidebarOpen(false);
    }
    window.scrollTo(0, 0);
  };

  const renderContent = () => {
    // Add key to force re-render when lesson changes
    const contentKey = currentLesson.id;

    switch (currentLesson.type) {
      case 'video':
        return (
          <div key={contentKey} className="aspect-video w-full overflow-hidden rounded-lg bg-black">
            <iframe
              src={currentLesson.content.videoUrl}
              className="h-full w-full"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            />
          </div>
        );
      case 'document':
        return (
          <div key={contentKey} className="flex flex-col items-center justify-center rounded-lg border-2 border-dashed border-border bg-muted/30 py-20">
            <FileText className="mb-4 h-16 w-16 text-muted-foreground" />
            <p className="mb-2 text-lg font-medium">{currentLesson.content.documentName}</p>
            {currentLesson.content.allowDownload && (
              <Button>
                <Download className="mr-2 h-4 w-4" />
                Download Document
              </Button>
            )}
          </div>
        );
      case 'image':
        return (
          <div key={contentKey} className="overflow-hidden rounded-lg">
            <img
              src={currentLesson.content.imageUrl}
              alt={currentLesson.title}
              className="w-full object-contain"
            />
          </div>
        );
      case 'quiz':
        return (
          <div key={contentKey} className="flex flex-col items-center justify-center rounded-lg border border-border bg-card py-20">
            <div className="mb-4 flex h-20 w-20 items-center justify-center rounded-full gradient-primary">
              <HelpCircle className="h-10 w-10 text-primary-foreground" />
            </div>
            <h3 className="mb-2 text-xl font-semibold">{currentLesson.title}</h3>
            <p className="mb-6 text-muted-foreground">Test your knowledge with this quiz</p>
            <Button size="lg" asChild>
              <Link to={`/course/${courseId}/quiz/${currentLesson.content.quizId}`}>
                Start Quiz
              </Link>
            </Button>
          </div>
        );
      default:
        return <p>Unsupported content type</p>;
    }
  };

  // Check if all lessons are completed
  const allLessonsCompleted = completedLessons.length === lessons.length && lessons.length > 0;



  return (
    <div className="flex min-h-screen bg-background relative">
      <GamificationPopup
        isOpen={showGamificationPopup}
        onClose={() => {
          setShowGamificationPopup(false);
          // Navigate to my-courses after closing
          navigate('/my-courses');
        }}
        pointsEarned={pointsEarned}
        currentBadge={currentBadge}
        nextBadge={nextBadge}
        progress={progressToNextBadge}
        title="Course Completed!"
        buttonText="Back to My Courses"
      />
      {/* Sidebar */}
      <aside
        className={cn(
          "fixed inset-y-0 left-0 z-40 flex w-80 flex-col border-r border-border bg-card transition-transform duration-300 lg:static lg:translate-x-0",
          sidebarOpen ? "translate-x-0" : "-translate-x-full"
        )}
      >
        {/* Header */}
        <div className="flex items-center justify-between border-b border-border p-4">
          <Link
            to="/my-courses"
            className="flex items-center gap-2 text-sm text-muted-foreground hover:text-foreground"
          >
            <ChevronLeft className="h-4 w-4" />
            Back to My Courses
          </Link>
          <Button
            variant="ghost"
            size="icon"
            className="lg:hidden"
            onClick={() => setSidebarOpen(false)}
          >
            <X className="h-5 w-5" />
          </Button>
        </div>

        {/* Course Info */}
        <div className="border-b border-border p-4">
          <h2 className="mb-2 font-semibold line-clamp-2">{course.title}</h2>
          <div className="mb-3">
            <div className="mb-1 flex items-center justify-between text-sm">
              <span className="text-muted-foreground">Progress</span>
              <span className="font-medium">{progress}%</span>
            </div>
            <Progress value={progress} className="h-2" />
          </div>
          <p className="text-sm text-muted-foreground">
            {completedLessons.length} of {lessons.length} lessons completed
          </p>
        </div>

        {/* Lesson List */}
        <div className="flex-1 overflow-y-auto">
          {lessons.map((lesson, index) => {
            const Icon = getLessonIcon(lesson.type);
            const isCompleted = completedLessons.includes(lesson.id);
            const isCurrent = index === currentLessonIndex;

            return (
              <button
                key={lesson.id}
                onClick={() => handleLessonSelect(index)}
                className={cn(
                  "flex w-full items-center gap-3 border-b border-border p-4 text-left transition-colors",
                  isCurrent && "bg-primary/5",
                  !isCurrent && "hover:bg-muted/50"
                )}
              >
                <div className={cn(
                  "flex h-8 w-8 flex-shrink-0 items-center justify-center rounded-lg text-sm",
                  isCompleted && "bg-success/10 text-success",
                  isCurrent && !isCompleted && "bg-primary/10 text-primary",
                  !isCompleted && !isCurrent && "bg-muted text-muted-foreground"
                )}>
                  {isCompleted ? (
                    <CheckCircle className="h-4 w-4" />
                  ) : (
                    <Icon className="h-4 w-4" />
                  )}
                </div>
                <div className="min-w-0 flex-1">
                  <p className={cn(
                    "truncate text-sm font-medium",
                    isCurrent && "text-primary"
                  )}>
                    {lesson.title}
                  </p>
                  <p className="text-xs text-muted-foreground capitalize">
                    {lesson.type} ‚Ä¢ {lesson.duration} min
                  </p>
                </div>
              </button>
            );
          })}
        </div>
      </aside>

      {/* Mobile Overlay */}
      {sidebarOpen && (
        <div
          className="fixed inset-0 z-30 bg-background/80 backdrop-blur-sm lg:hidden"
          onClick={() => setSidebarOpen(false)}
        />
      )}

      {/* Main Content */}
      <main className="flex flex-1 flex-col">
        {/* Top Bar */}
        <header className="flex items-center gap-4 border-b border-border bg-card px-4 py-3">
          <Button
            variant="ghost"
            size="icon"
            className="lg:hidden"
            onClick={() => setSidebarOpen(true)}
          >
            <Menu className="h-5 w-5" />
          </Button>
          <div className="flex-1">
            <h1 className="font-semibold">{currentLesson.title}</h1>
            <p className="text-sm text-muted-foreground capitalize">
              {currentLesson.type} ‚Ä¢ Lesson {currentLessonIndex + 1} of {lessons.length}
            </p>
          </div>
        </header>

        {/* Content Area */}
        <div className="flex-1 overflow-y-auto p-4 md:p-8">
          <div className="mx-auto max-w-4xl">
            {renderContent()}

            {/* Description */}
            {currentLesson.description && (
              <div className="mt-6">
                <h3 className="mb-2 font-semibold">About this lesson</h3>
                <p className="text-muted-foreground">{currentLesson.description}</p>
              </div>
            )}

            {/* Attachments */}
            {currentLesson.attachments.length > 0 && (
              <div className="mt-6">
                <h3 className="mb-3 flex items-center gap-2 font-semibold">
                  <Paperclip className="h-4 w-4" />
                  Attachments
                </h3>
                <div className="space-y-2">
                  {currentLesson.attachments.map((attachment) => (
                    <a
                      key={attachment.id}
                      href={attachment.url}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="flex items-center gap-3 rounded-lg border border-border bg-card p-3 transition-colors hover:bg-muted/50"
                    >
                      {attachment.type === 'file' ? (
                        <Download className="h-4 w-4 text-muted-foreground" />
                      ) : (
                        <ExternalLink className="h-4 w-4 text-muted-foreground" />
                      )}
                      <span className="flex-1 text-sm font-medium">{attachment.name}</span>
                    </a>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Navigation */}
        <footer className="flex items-center justify-between border-t border-border bg-card p-4">
          <Button
            variant="outline"
            onClick={goToPrevious}
            disabled={currentLessonIndex === 0}
          >
            <ChevronLeft className="mr-2 h-4 w-4" />
            Previous
          </Button>
          <Button
            onClick={currentLessonIndex === lessons.length - 1 ? markComplete : goToNext}
            disabled={courseCompleted}
            className={currentLessonIndex === lessons.length - 1 ? "" : ""}
            variant={currentLessonIndex === lessons.length - 1 ? "secondary" : "default"}
          >
            {currentLessonIndex === lessons.length - 1 ? (
              <>
                Finish Lesson
                <CheckCircle className="ml-2 h-4 w-4" />
              </>
            ) : (
              <>
                Next
                <ChevronRight className="ml-2 h-4 w-4" />
              </>
            )}
          </Button>
        </footer >

        {/* Course Completion Button (bottom-right) */}
        {
          allLessonsCompleted && !courseCompleted && (
            <div className="fixed bottom-6 right-6 z-50">
              <Button size="lg" onClick={handleCompleteCourse}>
                Complete this course
              </Button>
            </div>
          )
        }

        {/* Success confirmation (optional) */}

      </main >
    </div >
  );
}

import { useState } from 'react';
import { Link } from 'react-router-dom';
import {
  Plus,
  Search,
  Grid,
  List,
  Share2,
  Eye,
  Clock,
  Users,
  MessageCircle,
  Mail,
  Copy,
  MoreHorizontal,
  Trash2,
  BookOpen,
  Edit,
  X
} from 'lucide-react';
import { toast } from 'sonner';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
  DropdownMenuSub,
  DropdownMenuSubTrigger,
  DropdownMenuSubContent,
} from '@/components/ui/dropdown-menu';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from '@/components/ui/dialog';
import { Label } from '@/components/ui/label';
import { mockCourses } from '@/data/mockData';
import { cn } from '@/lib/utils';

export default function BackofficeCoursesPage() {
  const [search, setSearch] = useState('');
  const [viewMode, setViewMode] = useState<'grid' | 'list'>('list');
  const [newCourseDialogOpen, setNewCourseDialogOpen] = useState(false);
  const [newCourseName, setNewCourseName] = useState('');
  const [newCourseType, setNewCourseType] = useState('video');
  const [newCourseWebsite, setNewCourseWebsite] = useState('');
  const [newCourseTags, setNewCourseTags] = useState<string[]>([]);
  const [newTag, setNewTag] = useState('');

  const [courses, setCourses] = useState<any[]>(mockCourses);

  const filteredCourses = courses.filter((course) =>
    course.title.toLowerCase().includes(search.toLowerCase())
  );

  const formatDuration = (minutes: number) => {
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return hours > 0 ? `${hours}:${mins.toString().padStart(2, '0')}` : `0:${mins.toString().padStart(2, '0')}`;
  };

  const handleAddTag = () => {
    if (newTag.trim() && !newCourseTags.includes(newTag.trim())) {
      setNewCourseTags([...newCourseTags, newTag.trim()]);
      setNewTag('');
    }
  };

  const handleRemoveTag = (tagToRemove: string) => {
    setNewCourseTags(newCourseTags.filter(tag => tag !== tagToRemove));
  };

  const handleCreateCourse = () => {
    const newCourse = {
      id: `new-course-${Date.now()}`,
      title: newCourseName,
      description: 'New course description',
      image: 'https://images.unsplash.com/photo-1516321318423-f06f85e504b3?w=800&h=450&fit=crop',
      tags: newCourseTags.length > 0 ? newCourseTags : ['New'],
      status: 'draft' as const,
      visibility: 'everyone' as const,
      accessRule: 'open' as const,
      instructorId: 'curr-user',
      instructorName: 'Current User',
      totalLessons: 0,
      totalDuration: 0,
      viewsCount: 0,
      enrolledCount: 0,
      rating: 0,
      reviewsCount: 0,
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString(),
      website: newCourseType === 'video' ? newCourseWebsite : undefined,
    };

    setCourses([newCourse, ...courses]);
    setNewCourseDialogOpen(false);
    setNewCourseName('');
    setNewCourseWebsite('');
    setNewCourseTags([]);
    setNewTag('');
    toast.success('Course created successfully');
  };

  const handleCopyLink = (courseId: string) => {
    navigator.clipboard.writeText(`${window.location.origin}/course/${courseId}`);
    toast.success('Course link copied to clipboard');
  };

  const handleShareWhatsapp = (courseId: string) => {
    const url = encodeURIComponent(`${window.location.origin}/course/${courseId}`);
    const text = encodeURIComponent('Check out this amazing course!');
    window.open(`https://wa.me/?text=${text}%20${url}`, '_blank');
  };

  const handleShareEmail = (courseId: string) => {
    const url = encodeURIComponent(`${window.location.origin}/course/${courseId}`);
    const subject = encodeURIComponent('Check out this course');
    const body = encodeURIComponent(`I found this great course and thought you might like it: ${window.location.origin}/course/${courseId}`);
    window.open(`https://mail.google.com/mail/?view=cm&fs=1&su=${subject}&body=${body}`, '_blank');
  };

  const handleDelete = (courseId: string) => {
    setCourses(courses.filter((course) => course.id !== courseId));
    toast.success('Course deleted successfully');
  };

  const handleShare = (courseId: string) => {
    handleCopyLink(courseId);
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold">Courses</h1>
          <p className="text-muted-foreground">
            Manage your courses and content
          </p>
        </div>
        <Dialog open={newCourseDialogOpen} onOpenChange={setNewCourseDialogOpen}>
          <DialogTrigger asChild>
            <Button>
              <Plus className="mr-2 h-4 w-4" />
              Create Course
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Create New Course</DialogTitle>
            </DialogHeader>
            <div className="grid gap-4 py-4">
              <div className="grid gap-2">
                <Label htmlFor="name">Course Name *</Label>
                <Input
                  id="name"
                  value={newCourseName}
                  onChange={(e) => setNewCourseName(e.target.value)}
                  placeholder="e.g. Advanced React Patterns"
                />
              </div>

              <div className="grid gap-2">
                <Label>Course Format</Label>
                <Select value={newCourseType} onValueChange={setNewCourseType}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="video">Video Course</SelectItem>
                    <SelectItem value="document">Text/Document Course</SelectItem>
                    <SelectItem value="interactive">Interactive Course</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              {newCourseType === 'video' && (
                <>
                  <div className="grid gap-2">
                    <Label htmlFor="website">Website *</Label>
                    <Input
                      id="website"
                      value={newCourseWebsite}
                      onChange={(e) => setNewCourseWebsite(e.target.value)}
                      placeholder="https://example.com"
                    />
                  </div>

                  <div className="grid gap-2">
                    <Label htmlFor="tags">Tags</Label>
                    <div className="flex gap-2">
                      <Input
                        id="tags"
                        value={newTag}
                        onChange={(e) => setNewTag(e.target.value)}
                        placeholder="Add a tag"
                        onKeyDown={(e) => {
                          if (e.key === 'Enter') {
                            e.preventDefault();
                            handleAddTag();
                          }
                        }}
                      />
                      <Button type="button" onClick={handleAddTag} size="sm" variant="secondary">
                        Add
                      </Button>
                    </div>
                    {newCourseTags.length > 0 && (
                      <div className="flex flex-wrap gap-2 mt-2">
                        {newCourseTags.map((tag) => (
                          <Badge key={tag} variant="secondary" className="gap-1 pr-1">
                            {tag}
                            <button
                              onClick={() => handleRemoveTag(tag)}
                              className="ml-1 rounded-full p-0.5 hover:bg-muted-foreground/20"
                            >
                              <X className="h-3 w-3" />
                            </button>
                          </Badge>
                        ))}
                      </div>
                    )}
                  </div>
                </>
              )}
            </div>
            <div className="flex justify-end gap-3">
              <Button variant="outline" onClick={() => setNewCourseDialogOpen(false)}>
                Cancel
              </Button>
              <Button onClick={handleCreateCourse} disabled={!newCourseName.trim()}>
                Create Course
              </Button>
            </div>
          </DialogContent>
        </Dialog>
      </div>

      {/* Search and View Toggle */}
      <div className="flex flex-col gap-4 sm:flex-row sm:items-center sm:justify-between">
        <div className="relative w-full sm:max-w-md">
          <Search className="absolute left-3 top-1/2 h-4 w-4 -translate-y-1/2 text-muted-foreground" />
          <Input
            placeholder="Search courses..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="pl-10"
          />
        </div>
        <div className="flex items-center gap-2">
          <Button
            variant={viewMode === 'grid' ? 'default' : 'outline'}
            size="icon"
            onClick={() => setViewMode('grid')}
            title="Kanban View"
          >
            <Grid className="h-4 w-4" />
          </Button>
          <Button
            variant={viewMode === 'list' ? 'default' : 'outline'}
            size="icon"
            onClick={() => setViewMode('list')}
            title="List View"
          >
            <List className="h-4 w-4" />
          </Button>
        </div>
      </div>

      {/* Courses List View (Matching Prototype) */}
      {viewMode === 'list' ? (
        <div className="space-y-4">
          {filteredCourses.map((course) => (
            <div
              key={course.id}
              className="group relative overflow-hidden rounded-xl border border-border bg-card shadow-sm transition-all hover:shadow-lg hover:border-primary/30"
            >
              {/* Background Image on Left Half */}
              <div
                className="absolute inset-y-0 left-0 w-1/3 bg-cover bg-center"
                style={{ backgroundImage: `url(${course.image})` }}
              >
                {/* Gradient Overlay */}
                <div className="absolute inset-0 bg-gradient-to-r from-transparent via-card/80 to-card" />
              </div>

              {/* Content */}
              <div className="relative flex flex-col sm:flex-row sm:items-center gap-4 p-5 pl-[35%]">
                {/* Course Info */}
                <div className="flex-1 min-w-0">
                  <h3 className="font-bold text-lg text-foreground mb-1 group-hover:text-primary transition-colors">
                    {course.title}
                  </h3>
                  <div className="flex flex-wrap gap-1">
                    {course.tags.map((tag: string, index: number) => (
                      <span
                        key={index}
                        className="inline-flex items-center text-xs text-accent font-medium"
                      >
                        {tag}
                        {index < course.tags.length - 1 && (
                          <span className="mx-1 text-muted-foreground">+</span>
                        )}
                      </span>
                    ))}
                  </div>
                </div>

                {/* Stats */}
                <div className="flex items-center gap-6 text-sm">
                  <div className="text-center px-3 py-1 rounded-lg bg-muted/50">
                    <p className="font-bold text-foreground">{course.viewsCount || 0}</p>
                    <p className="text-xs text-muted-foreground">Views</p>
                  </div>
                  <div className="text-center px-3 py-1 rounded-lg bg-muted/50">
                    <p className="font-bold text-foreground">{course.totalLessons}</p>
                    <p className="text-xs text-muted-foreground">Contents</p>
                  </div>
                  <div className="text-center px-3 py-1 rounded-lg bg-muted/50">
                    <p className="font-bold text-foreground">{formatDuration(course.totalDuration)}</p>
                    <p className="text-xs text-muted-foreground">Duration</p>
                  </div>
                </div>

                {/* Actions */}
                <div className="flex items-center gap-2">
                  <Button
                    variant="outline"
                    size="sm"
                    className="bg-card/80 backdrop-blur-sm hover:bg-accent hover:text-accent-foreground"
                    onClick={() => handleShare(course.id)}
                  >
                    <Share2 className="mr-2 h-4 w-4" />
                    Share
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    className="bg-card/80 backdrop-blur-sm hover:bg-primary hover:text-primary-foreground"
                    asChild
                  >
                    <Link to={`/backoffice/courses/${course.id}`}>
                      <Edit className="mr-2 h-4 w-4" />
                      Edit
                    </Link>
                  </Button>
                </div>

                {/* Status Badge */}
                <div>
                  <Badge
                    className={cn(
                      "capitalize font-medium",
                      course.status === 'published'
                        ? 'bg-success/20 text-success border-success/30'
                        : 'bg-muted text-muted-foreground border-muted-foreground/30'
                    )}
                  >
                    {course.status}
                  </Badge>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        /* Grid View (Kanban Style) */
        <div className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3">
          {filteredCourses.map((course) => (
            <Card
              key={course.id}
              className="group overflow-hidden transition-all hover:shadow-card-hover"
            >
              {/* Image */}
              <div className="relative aspect-video overflow-hidden">
                <img
                  src={course.image}
                  alt={course.title}
                  className="h-full w-full object-cover transition-transform duration-300 group-hover:scale-105"
                />
                <div className="absolute right-2 top-2">
                  <Badge
                    className={cn(
                      "capitalize",
                      course.status === 'published'
                        ? 'bg-success/10 text-success border-success/30 backdrop-blur-sm'
                        : 'bg-muted/80 text-muted-foreground backdrop-blur-sm'
                    )}
                  >
                    {course.status}
                  </Badge>
                </div>
              </div>

              {/* Content */}
              <CardContent className="p-4">
                {/* Tags */}
                <div className="mb-2 flex flex-wrap gap-1">
                  {course.tags.slice(0, 3).map((tag: string, index: number) => (
                    <span
                      key={index}
                      className="inline-flex items-center text-xs text-accent"
                    >
                      {tag}
                      {index < Math.min(course.tags.length, 3) - 1 && (
                        <span className="mx-1 text-muted-foreground">+</span>
                      )}
                    </span>
                  ))}
                </div>

                {/* Title */}
                <h3 className="mb-3 line-clamp-2 font-semibold">{course.title}</h3>

                {/* Stats */}
                <div className="mb-4 flex items-center gap-4 text-sm text-muted-foreground">
                  <span className="flex items-center gap-1">
                    <Eye className="h-4 w-4" />
                    {course.viewsCount || 0}
                  </span>
                  <span className="flex items-center gap-1">
                    <BookOpen className="h-4 w-4" />
                    {course.totalLessons}
                  </span>
                  <span className="flex items-center gap-1">
                    <Clock className="h-4 w-4" />
                    {formatDuration(course.totalDuration)}
                  </span>
                  <span className="flex items-center gap-1">
                    <Users className="h-4 w-4" />
                    {course.enrolledCount} enrolled
                  </span>
                </div>
                <div className="flex items-center justify-between">
                  <Button variant="outline" size="sm" asChild>
                    <Link to={`/backoffice/courses/${course.id}`}>
                      <Edit className="mr-2 h-4 w-4" />
                      Edit
                    </Link>
                  </Button>
                  <DropdownMenu>
                    <DropdownMenuTrigger asChild>
                      <Button variant="ghost" size="icon">
                        <MoreHorizontal className="h-4 w-4" />
                      </Button>
                    </DropdownMenuTrigger>
                    <DropdownMenuContent align="end">
                      <DropdownMenuSub>
                        <DropdownMenuSubTrigger className="cursor-pointer">
                          <Share2 className="mr-2 h-4 w-4" />
                          Share
                        </DropdownMenuSubTrigger>
                        <DropdownMenuSubContent>
                          <DropdownMenuItem onClick={() => handleShareWhatsapp(course.id)} className="cursor-pointer">
                            <MessageCircle className="mr-2 h-4 w-4" />
                            WhatsApp
                          </DropdownMenuItem>
                          <DropdownMenuItem onClick={() => handleShareEmail(course.id)} className="cursor-pointer">
                            <Mail className="mr-2 h-4 w-4" />
                            Gmail
                          </DropdownMenuItem>
                          <DropdownMenuItem onClick={() => handleCopyLink(course.id)} className="cursor-pointer">
                            <Copy className="mr-2 h-4 w-4" />
                            Copy Link
                          </DropdownMenuItem>
                        </DropdownMenuSubContent>
                      </DropdownMenuSub>
                      <DropdownMenuItem asChild className="cursor-pointer">
                        <Link to={`/course/${course.id}`}>
                          <Eye className="mr-2 h-4 w-4" />
                          Preview
                        </Link>
                      </DropdownMenuItem>
                      <DropdownMenuSeparator />
                      <DropdownMenuItem
                        onClick={() => handleDelete(course.id)}
                        className="text-destructive cursor-pointer"
                      >
                        <Trash2 className="mr-2 h-4 w-4" />
                        Delete
                      </DropdownMenuItem>
                    </DropdownMenuContent>
                  </DropdownMenu>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}

      {/* Empty State */}
      {
        filteredCourses.length === 0 && (
          <div className="flex flex-col items-center justify-center py-20 text-center">
            <div className="mb-4 rounded-full bg-muted p-4">
              <BookOpen className="h-8 w-8 text-muted-foreground" />
            </div>
            <h3 className="mb-2 text-lg font-semibold">No courses found</h3>
            <p className="text-muted-foreground">
              {search ? 'Try a different search term' : 'No courses available'}
            </p>
          </div>
        )
      }
    </div >
  );
}

import { useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import {
  ChevronLeft,
  Save,
  Eye,
  Users,
  Mail,
  Upload,
  Plus,
  Edit,
  Trash2,
  GripVertical,
  Play,
  FileText,
  Image,
  HelpCircle,
  Share2,
  X
} from 'lucide-react';
import { toast } from 'sonner';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Switch } from '@/components/ui/switch';
import { Badge } from '@/components/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Checkbox } from '@/components/ui/checkbox';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from '@/components/ui/dialog';
import { mockCourses, mockLessons } from '@/data/mockData';
import { LessonType } from '@/types';
import { cn } from '@/lib/utils';

const getLessonIcon = (type: LessonType) => {
  switch (type) {
    case 'video': return Play;
    case 'document': return FileText;
    case 'image': return Image;
    case 'quiz': return HelpCircle;
    default: return FileText;
  }
};

export default function CourseEditorPage() {
  const { courseId } = useParams();
  const course = mockCourses.find((c) => c.id === courseId);
  const lessons = mockLessons.filter((l) => l.courseId === courseId);
  // @ts-ignore - Mock data typing shortcut
  const [lessonsList, setLessonsList] = useState<any[]>(lessons);

  const [isPublished, setIsPublished] = useState(course?.status === 'published');
  const [title, setTitle] = useState(course?.title || '');
  const [description, setDescription] = useState(course?.description || '');
  const [visibility, setVisibility] = useState<string>(course?.visibility || 'everyone');
  const [accessRule, setAccessRule] = useState<string>(course?.accessRule || 'open');
  const [price, setPrice] = useState(course?.price?.toString() || '');
  const [website, setWebsite] = useState(course?.website || '');
  const [responsibleId, setResponsibleId] = useState(course?.instructorId || '');
  const [courseImage, setCourseImage] = useState(course?.image || '');
  const [addAttendeeOpen, setAddAttendeeOpen] = useState(false);
  const [contactAttendeeOpen, setContactAttendeeOpen] = useState(false);
  const [lessonDialogOpen, setLessonDialogOpen] = useState(false);
  const [newLessonTitle, setNewLessonTitle] = useState('');
  const [newLessonType, setNewLessonType] = useState<LessonType>('video');
  const [newLessonWebsite, setNewLessonWebsite] = useState('');
  const [newLessonTags, setNewLessonTags] = useState<string[]>([]);
  const [newLessonTag, setNewLessonTag] = useState('');
  const [editingLessonId, setEditingLessonId] = useState<string | null>(null);

  // Quiz State
  const [quizQuestions, setQuizQuestions] = useState([
    {
      id: 'q1',
      text: '',
      choices: [
        { id: 'c1', text: '', isCorrect: false },
        { id: 'c2', text: '', isCorrect: false },
        { id: 'c3', text: '', isCorrect: false },
      ]
    }
  ]);
  const [activeQuizView, setActiveQuizView] = useState<string>('q1');
  const [rewards, setRewards] = useState({
    first: 10,
    second: 7,
    third: 5,
    fourth: 2
  });

  const handleAddQuestion = () => {
    const newId = `q${Date.now()}`;
    setQuizQuestions([
      ...quizQuestions,
      {
        id: newId,
        text: '',
        choices: [
          { id: `c${Date.now()}-1`, text: '', isCorrect: false },
          { id: `c${Date.now()}-2`, text: '', isCorrect: false },
        ]
      }
    ]);
    setActiveQuizView(newId);
  };

  const updateQuestionText = (id: string, text: string) => {
    setQuizQuestions(quizQuestions.map(q => q.id === id ? { ...q, text } : q));
  };

  const updateChoice = (qId: string, cId: string, field: string, value: any) => {
    setQuizQuestions(quizQuestions.map(q => q.id === qId ? {
      ...q,
      choices: q.choices.map(c => c.id === cId ? { ...c, [field]: value } : c)
    } : q));
  };

  const addChoice = (qId: string) => {
    setQuizQuestions(quizQuestions.map(q => q.id === qId ? {
      ...q,
      choices: [...q.choices, { id: `c${Date.now()}`, text: '', isCorrect: false }]
    } : q));
  };

  const removeChoice = (qId: string, cId: string) => {
    setQuizQuestions(quizQuestions.map(q => q.id === qId ? {
      ...q,
      choices: q.choices.filter(c => c.id !== cId)
    } : q));
  };

  const handleImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setCourseImage(reader.result as string);
        toast.success('Course image updated successfully');
      };
      reader.readAsDataURL(file);
    }
  };

  const handleContactAttendee = () => {
    // Mock implementation
    setContactAttendeeOpen(false);
    toast.success('Message sent to attendees successfully');
  };

  const handleAddLessonTag = () => {
    if (newLessonTag.trim() && !newLessonTags.includes(newLessonTag.trim())) {
      setNewLessonTags([...newLessonTags, newLessonTag.trim()]);
      setNewLessonTag('');
    }
  };

  const handleRemoveLessonTag = (tagToRemove: string) => {
    setNewLessonTags(newLessonTags.filter(tag => tag !== tagToRemove));
  };

  const handleEditLesson = (lesson: any) => {
    setEditingLessonId(lesson.id);
    setNewLessonTitle(lesson.title);
    setNewLessonType(lesson.type);
    setNewLessonWebsite(lesson.website || '');
    setNewLessonTags(lesson.tags || []);
    setLessonDialogOpen(true);
  };

  const handleDeleteLesson = (lessonId: string) => {
    setLessonsList(lessonsList.filter(l => l.id !== lessonId));
    toast.success('Lesson deleted successfully');
  };

  const handleSaveLesson = () => {
    if (editingLessonId) {
      setLessonsList(lessonsList.map(l => l.id === editingLessonId ? {
        ...l,
        title: newLessonTitle,
        type: newLessonType,
        website: newLessonWebsite,
        tags: newLessonTags,
        updatedAt: new Date().toISOString()
      } : l));
      toast.success('Lesson updated successfully');
    } else {
      const newLesson = {
        id: `new-lesson-${Date.now()}`,
        courseId: courseId!,
        title: newLessonTitle,
        description: '',
        type: newLessonType,
        order: lessonsList.length + 1,
        duration: 10,
        content: {},
        website: newLessonWebsite,
        tags: newLessonTags,
        attachments: [],
        createdAt: new Date().toISOString(),
        updatedAt: new Date().toISOString()
      };
      setLessonsList([...lessonsList, newLesson]);
      toast.success('Lesson added successfully');
    }
    setLessonDialogOpen(false);
    setNewLessonTitle('');
    setNewLessonType('video');
    setNewLessonWebsite('');
    setNewLessonTags([]);
    setNewLessonTag('');
    setEditingLessonId(null);
  };

  const handleAddAttendee = () => {
    // Mock implementation
    setAddAttendeeOpen(false);
    toast.success('Attendee added successfully');
  };

  if (!course) {
    return (
      <div className="flex items-center justify-center p-20">
        <div className="text-center">
          <h1 className="mb-4 text-2xl font-bold">Course not found</h1>
          <Button asChild>
            <Link to="/backoffice/courses">Back to Courses</Link>
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col gap-4 lg:flex-row lg:items-center lg:justify-between">
        <div className="flex items-center gap-4">
          <Button variant="ghost" size="icon" asChild>
            <Link to="/backoffice/courses">
              <ChevronLeft className="h-5 w-5" />
            </Link>
          </Button>
          <div>
            <h1 className="text-2xl font-bold">Edit Course</h1>
            <p className="text-sm text-muted-foreground">{course.title}</p>
          </div>
        </div>
        <div className="flex flex-wrap items-center gap-3">
          <div className="flex items-center gap-2">
            <span className="text-sm text-muted-foreground">Published</span>
            <Switch checked={isPublished} onCheckedChange={setIsPublished} />
          </div>
          <Button variant="outline" asChild>
            <Link to={`/course/${courseId}`}>
              <Eye className="mr-2 h-4 w-4" />
              Preview
            </Link>
          </Button>
          <Button variant="outline" onClick={() => setAddAttendeeOpen(true)}>
            <Users className="mr-2 h-4 w-4" />
            Add Attendees
          </Button>
          <Button variant="outline" onClick={() => setContactAttendeeOpen(true)}>
            <Mail className="mr-2 h-4 w-4" />
            Contact
          </Button>
          <Button onClick={() => toast.success('Course changes saved')}>
            <Save className="mr-2 h-4 w-4" />
            Save Changes
          </Button>
        </div>
      </div>

      {/* Course Image */}
      <div className="flex items-start gap-6">
        <div className="relative h-40 w-64 flex-shrink-0 overflow-hidden rounded-xl border-2 border-dashed border-border bg-muted/30 group">
          <input
            type="file"
            accept="image/*"
            className="absolute inset-0 z-20 h-full w-full cursor-pointer opacity-0"
            onChange={handleImageUpload}
          />
          {courseImage ? (
            <img
              src={courseImage}
              alt={title || 'Course Image'}
              className="h-full w-full object-cover transition-opacity group-hover:opacity-80"
            />
          ) : (
            <div className="flex h-full items-center justify-center">
              <Upload className="h-8 w-8 text-muted-foreground" />
            </div>
          )}
          <Button
            size="sm"
            variant="secondary"
            className="absolute bottom-2 right-2 z-10 pointers-events-none"
          >
            Change Image
          </Button>
        </div>
        <div className="flex-1 space-y-4">
          <div>
            <Label htmlFor="title">Course Title <span className="text-destructive">*</span></Label>
            <Input
              id="title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              placeholder="Enter course title..."
              className="mt-1"
            />
          </div>

          <div className="grid gap-4 md:grid-cols-2">
            <div>
              <Label>Responsible</Label>
              <Select value={responsibleId} onValueChange={setResponsibleId}>
                <SelectTrigger className="mt-1">
                  <SelectValue placeholder="Select course admin..." />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="inst-1">Sarah Johnson</SelectItem>
                  <SelectItem value="inst-2">Michael Chen</SelectItem>
                  <SelectItem value="inst-3">Emily Rodriguez</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>


        </div>
      </div>

      {/* Tabs */}
      <Tabs defaultValue="content" className="space-y-6">
        <TabsList>
          <TabsTrigger value="content">Content</TabsTrigger>
          <TabsTrigger value="description">Description</TabsTrigger>
          <TabsTrigger value="options">Options</TabsTrigger>
          <TabsTrigger value="quiz">Quiz Settings</TabsTrigger>
        </TabsList>

        {/* Content Tab */}
        <TabsContent value="content" className="space-y-4">
          <div className="flex items-center justify-between">
            <h3 className="text-lg font-semibold">Lessons ({lessonsList.length})</h3>
            <Dialog open={lessonDialogOpen} onOpenChange={setLessonDialogOpen}>
              <DialogTrigger asChild>
                <Button>
                  <Plus className="mr-2 h-4 w-4" />
                  Add Content
                </Button>
              </DialogTrigger>
              <DialogContent>
                <DialogHeader>
                  <DialogTitle>{editingLessonId ? 'Edit Lesson' : 'Add New Lesson'}</DialogTitle>
                </DialogHeader>
                <div className="space-y-4 pt-4">
                  <div>
                    <Label htmlFor="lesson-title">Lesson Title</Label>
                    <Input
                      id="lesson-title"
                      value={newLessonTitle}
                      onChange={(e) => setNewLessonTitle(e.target.value)}
                      placeholder="Enter lesson title..."
                      className="mt-1"
                    />
                  </div>
                  <div>
                    <Label>Lesson Type</Label>
                    <div className="mt-2 grid grid-cols-2 gap-2">
                      {(['video', 'document', 'image', 'quiz'] as LessonType[]).map((type) => {
                        const Icon = getLessonIcon(type);
                        return (
                          <button
                            key={type}
                            onClick={() => setNewLessonType(type)}
                            className={cn(
                              "flex items-center gap-2 rounded-lg border-2 p-3 text-left transition-colors",
                              newLessonType === type
                                ? "border-primary bg-primary/5"
                                : "border-border hover:border-primary/50"
                            )}
                          >
                            <Icon className="h-5 w-5" />
                            <span className="capitalize">{type}</span>
                          </button>
                        );
                      })}
                    </div>
                  </div>

                  {newLessonType === 'video' && (
                    <>
                      <div className="grid gap-2">
                        <Label htmlFor="lesson-website">Website</Label>
                        <Input
                          id="lesson-website"
                          value={newLessonWebsite}
                          onChange={(e) => setNewLessonWebsite(e.target.value)}
                          placeholder="https://example.com"
                        />
                      </div>

                      <div className="grid gap-2">
                        <Label>Tags</Label>
                        <div className="flex gap-2">
                          <Input
                            value={newLessonTag}
                            onChange={(e) => setNewLessonTag(e.target.value)}
                            placeholder="Add a tag"
                            onKeyDown={(e) => {
                              if (e.key === 'Enter') {
                                e.preventDefault();
                                handleAddLessonTag();
                              }
                            }}
                          />
                          <Button type="button" onClick={handleAddLessonTag} size="sm" variant="secondary">
                            Add
                          </Button>
                        </div>
                        {newLessonTags.length > 0 && (
                          <div className="flex flex-wrap gap-2 mt-2">
                            {newLessonTags.map((tag) => (
                              <Badge key={tag} variant="secondary" className="gap-1 pr-1">
                                {tag}
                                <button
                                  onClick={() => handleRemoveLessonTag(tag)}
                                  className="ml-1 rounded-full p-0.5 hover:bg-muted-foreground/20"
                                >
                                  <X className="h-3 w-3" />
                                </button>
                              </Badge>
                            ))}
                          </div>
                        )}
                      </div>
                    </>
                  )}

                  <div className="flex justify-end gap-2 pt-4">
                    <Button variant="outline" onClick={() => {
                      setLessonDialogOpen(false);
                      setEditingLessonId(null);
                      setNewLessonTitle('');
                      setNewLessonWebsite('');
                      setNewLessonTags([]);
                    }}>
                      Cancel
                    </Button>
                    <Button disabled={!newLessonTitle.trim()} onClick={handleSaveLesson}>
                      {editingLessonId ? 'Save Changes' : 'Add Lesson'}
                    </Button>
                  </div>
                </div>
              </DialogContent>
            </Dialog>
          </div>

          <div className="rounded-xl border border-border bg-card">
            {lessonsList.length > 0 ? (
              <div className="divide-y divide-border">
                {lessonsList.map((lesson) => {
                  const Icon = getLessonIcon(lesson.type);
                  return (
                    <div
                      key={lesson.id}
                      className="flex items-center gap-4 p-4 transition-colors hover:bg-muted/50"
                    >
                      <GripVertical className="h-5 w-5 cursor-grab text-muted-foreground" />
                      <div className={cn(
                        "flex h-10 w-10 flex-shrink-0 items-center justify-center rounded-lg",
                        "bg-primary/10 text-primary"
                      )}>
                        <Icon className="h-5 w-5" />
                      </div>
                      <div className="min-w-0 flex-1">
                        <p className="font-medium">{lesson.title}</p>
                        <p className="text-sm text-muted-foreground capitalize">
                          {lesson.type} ‚Ä¢ {lesson.duration} min
                        </p>
                      </div>
                      <div className="flex items-center gap-2">
                        <Button variant="ghost" size="icon" onClick={() => handleEditLesson(lesson)}>
                          <Edit className="h-4 w-4" />
                        </Button>
                        <Button variant="ghost" size="icon" className="text-destructive" onClick={() => handleDeleteLesson(lesson.id)}>
                          <Trash2 className="h-4 w-4" />
                        </Button>
                      </div>
                    </div>
                  );
                })}
              </div>
            ) : (
              <div className="flex flex-col items-center justify-center py-12 text-center">
                <FileText className="mb-4 h-12 w-12 text-muted-foreground" />
                <h3 className="mb-2 font-semibold">No lessons yet</h3>
                <p className="mb-4 text-sm text-muted-foreground">
                  Add your first lesson to get started
                </p>
                <Button onClick={() => setLessonDialogOpen(true)}>
                  <Plus className="mr-2 h-4 w-4" />
                  Add Content
                </Button>
              </div>
            )}
          </div>
        </TabsContent>

        {/* Description Tab */}
        <TabsContent value="description">
          <div className="rounded-xl border border-border bg-card p-6">
            <Label htmlFor="description">Course Description</Label>
            <Textarea
              id="description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="Write a detailed description of your course..."
              className="mt-2 min-h-[200px]"
            />
          </div>
        </TabsContent>

        {/* Options Tab */}
        <TabsContent value="options">
          <div className="rounded-xl border border-border bg-card p-6">
            <div className="grid gap-6 md:grid-cols-2">
              <div>
                <Label>Visibility</Label>
                <Select value={visibility} onValueChange={setVisibility}>
                  <SelectTrigger className="mt-2">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="everyone">Everyone</SelectItem>
                    <SelectItem value="signed_in">Signed In Users Only</SelectItem>
                  </SelectContent>
                </Select>
                <p className="mt-1 text-sm text-muted-foreground">
                  Who can see this course in the catalog
                </p>
              </div>

              <div>
                <Label>Access Rule</Label>
                <Select value={accessRule} onValueChange={setAccessRule}>
                  <SelectTrigger className="mt-2">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="open">Open (Free)</SelectItem>
                    <SelectItem value="invitation">On Invitation</SelectItem>
                    <SelectItem value="payment">On Payment</SelectItem>
                  </SelectContent>
                </Select>
                <p className="mt-1 text-sm text-muted-foreground">
                  How users can access this course
                </p>
              </div>

              {accessRule === 'payment' && (
                <div>
                  <Label htmlFor="price">Price ($)</Label>
                  <Input
                    id="price"
                    type="number"
                    value={price}
                    onChange={(e) => setPrice(e.target.value)}
                    placeholder="49.99"
                    className="mt-2"
                  />
                </div>
              )}


            </div>
          </div>
        </TabsContent>

        {/* Quiz Tab */}
        <TabsContent value="quiz" className="h-[600px] flex flex-col">
          <div className="flex-1 flex rounded-xl border border-border bg-card overflow-hidden">
            {/* Sidebar */}
            <div className="w-64 border-r border-border bg-muted/20 flex flex-col">
              <div className="p-4 border-b border-border">
                <h3 className="font-semibold">Question List</h3>
              </div>
              <div className="flex-1 overflow-y-auto p-2 space-y-1">
                {quizQuestions.map((q, idx) => (
                  <button
                    key={q.id}
                    onClick={() => setActiveQuizView(q.id)}
                    className={cn(
                      "w-full text-left px-3 py-2 rounded-md text-sm transition-colors flex items-center justify-between group",
                      activeQuizView === q.id
                        ? "bg-primary/10 text-primary font-medium"
                        : "hover:bg-muted text-muted-foreground hover:text-foreground"
                    )}
                  >
                    <span>Question {idx + 1}</span>
                    {quizQuestions.length > 1 && (
                      <Trash2
                        className="h-3 w-3 opacity-0 group-hover:opacity-100 text-destructive"
                        onClick={(e) => {
                          e.stopPropagation();
                          setQuizQuestions(quizQuestions.filter(item => item.id !== q.id));
                          if (activeQuizView === q.id) setActiveQuizView('rewards');
                        }}
                      />
                    )}
                  </button>
                ))}
              </div>
              <div className="p-4 border-t border-border space-y-2">
                <Button onClick={handleAddQuestion} className="w-full bg-indigo-500 hover:bg-indigo-600 font-medium">
                  Add Question
                </Button>
                <Button
                  onClick={() => setActiveQuizView('rewards')}
                  className={cn(
                    "w-full font-medium transition-colors",
                    activeQuizView === 'rewards'
                      ? "bg-purple-600 text-white hover:bg-purple-700"
                      : "bg-purple-100 text-purple-700 hover:bg-purple-200"
                  )}
                >
                  Rewards
                </Button>
              </div>
            </div>

            {/* Content */}
            <div className="flex-1 bg-background p-8 overflow-y-auto">
              {activeQuizView === 'rewards' ? (
                <div className="max-w-xl mx-auto">
                  <h2 className="text-xl font-bold mb-8">Rewards settings</h2>
                  <div className="space-y-6">
                    <div className="grid gap-2">
                      <div className="flex items-center justify-between">
                        <Label className="text-base text-yellow-600">First try :</Label>
                        <div className="relative w-32">
                          <Input
                            type="number"
                            value={rewards.first}
                            onChange={(e) => setRewards({ ...rewards, first: parseInt(e.target.value) })}
                            className="pr-12 text-center"
                          />
                          <span className="absolute right-3 top-1/2 -translate-y-1/2 text-sm text-yellow-600">points</span>
                        </div>
                      </div>
                    </div>
                    <div className="grid gap-2">
                      <div className="flex items-center justify-between">
                        <Label className="text-base text-yellow-600">Second try :</Label>
                        <div className="relative w-32">
                          <Input
                            type="number"
                            value={rewards.second}
                            onChange={(e) => setRewards({ ...rewards, second: parseInt(e.target.value) })}
                            className="pr-12 text-center"
                          />
                          <span className="absolute right-3 top-1/2 -translate-y-1/2 text-sm text-yellow-600">points</span>
                        </div>
                      </div>
                    </div>
                    <div className="grid gap-2">
                      <div className="flex items-center justify-between">
                        <Label className="text-base text-yellow-600">Third try :</Label>
                        <div className="relative w-32">
                          <Input
                            type="number"
                            value={rewards.third}
                            onChange={(e) => setRewards({ ...rewards, third: parseInt(e.target.value) })}
                            className="pr-12 text-center"
                          />
                          <span className="absolute right-3 top-1/2 -translate-y-1/2 text-sm text-yellow-600">points</span>
                        </div>
                      </div>
                    </div>
                    <div className="grid gap-2">
                      <div className="flex items-center justify-between">
                        <Label className="text-base text-yellow-600">Fourth Try and more :</Label>
                        <div className="relative w-32">
                          <Input
                            type="number"
                            value={rewards.fourth}
                            onChange={(e) => setRewards({ ...rewards, fourth: parseInt(e.target.value) })}
                            className="pr-12 text-center"
                          />
                          <span className="absolute right-3 top-1/2 -translate-y-1/2 text-sm text-yellow-600">points</span>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              ) : (
                (() => {
                  const activeQuestion = quizQuestions.find(q => q.id === activeQuizView);
                  const index = quizQuestions.findIndex(q => q.id === activeQuizView);

                  if (!activeQuestion) return <div className="text-center text-muted-foreground">Select a question to edit</div>;

                  return (
                    <div className="max-w-3xl mx-auto space-y-8">
                      {/* Question Header */}
                      <div className="flex items-baseline gap-4">
                        <span className="text-2xl font-bold">{index + 1}.</span>
                        <div className="flex-1">
                          <Input
                            value={activeQuestion.text}
                            onChange={(e) => updateQuestionText(activeQuestion.id, e.target.value)}
                            className="text-lg border-0 border-b border-border rounded-none px-0 focus-visible:ring-0 focus-visible:border-primary bg-transparent placeholder:text-muted-foreground/50"
                            placeholder="Write your question here"
                          />
                        </div>
                      </div>

                      {/* Answers */}
                      <div>
                        <div className="grid grid-cols-[1fr,80px,40px] gap-4 mb-4 px-2 text-sm font-medium text-muted-foreground">
                          <div>Choices</div>
                          <div className="text-center">Correct</div>
                          <div></div>
                        </div>
                        <div className="space-y-3">
                          {activeQuestion.choices.map((choice) => (
                            <div key={choice.id} className="grid grid-cols-[1fr,80px,40px] items-center gap-4 p-2 rounded-lg hover:bg-muted/30 group">
                              <Input
                                value={choice.text}
                                onChange={(e) => updateChoice(activeQuestion.id, choice.id, 'text', e.target.value)}
                                className="bg-transparent"
                                placeholder={`Answer`}
                              />
                              <div className="flex justify-center">
                                <Checkbox
                                  checked={choice.isCorrect}
                                  onCheckedChange={(checked) => updateChoice(activeQuestion.id, choice.id, 'isCorrect', checked)}
                                  className="data-[state=checked]:bg-green-600 data-[state=checked]:border-green-600"
                                />
                              </div>
                              <Button
                                variant="ghost"
                                size="icon"
                                className="opacity-0 group-hover:opacity-100 text-destructive h-8 w-8"
                                onClick={() => removeChoice(activeQuestion.id, choice.id)}
                              >
                                <X className="h-4 w-4" />
                              </Button>
                            </div>
                          ))}
                        </div>
                        <Button
                          variant="ghost"
                          className="mt-4 text-blue-500 hover:text-blue-600 hover:bg-blue-50 pl-0"
                          onClick={() => addChoice(activeQuestion.id)}
                        >
                          <Plus className="mr-2 h-4 w-4" />
                          Add choice
                        </Button>
                      </div>
                    </div>
                  );
                })()
              )}
            </div>
          </div>
        </TabsContent>
      </Tabs>

      {/* Add Attendee Wizard */}
      <Dialog open={addAttendeeOpen} onOpenChange={setAddAttendeeOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Add Attendee</DialogTitle>
            <DialogDescription>
              Add a new learner to this course directly by email.
            </DialogDescription>
          </DialogHeader>
          <div className="py-4">
            <Label htmlFor="learner-email">Learner Email</Label>
            <Input id="learner-email" placeholder="learner@example.com" className="mt-2" />
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setAddAttendeeOpen(false)}>Cancel</Button>
            <Button onClick={handleAddAttendee}>Add Learner</Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Contact Wizard */}
      <Dialog open={contactAttendeeOpen} onOpenChange={setContactAttendeeOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Contact Attendees</DialogTitle>
            <DialogDescription>
              Send an email to all learners enrolled in this course.
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div>
              <Label htmlFor="subject">Subject</Label>
              <Input id="subject" placeholder="Course Update..." className="mt-2" />
            </div>
            <div>
              <Label htmlFor="message">Message</Label>
              <Textarea id="message" placeholder="Type your message here..." className="mt-2 min-h-[100px]" />
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setContactAttendeeOpen(false)}>Cancel</Button>
            <Button onClick={handleContactAttendee}>Send Message</Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

import type { Config } from "tailwindcss";

export default {
  darkMode: ["class"],
  content: ["./pages/**/*.{ts,tsx}", "./components/**/*.{ts,tsx}", "./app/**/*.{ts,tsx}", "./src/**/*.{ts,tsx}"],
  prefix: "",
  theme: {
    container: {
      center: true,
      padding: "2rem",
      screens: {
        "2xl": "1400px",
      },
    },
    extend: {
      colors: {
        border: "hsl(var(--border))",
        input: "hsl(var(--input))",
        ring: "hsl(var(--ring))",
        background: "hsl(var(--background))",
        foreground: "hsl(var(--foreground))",
        primary: {
          DEFAULT: "hsl(var(--primary))",
          foreground: "hsl(var(--primary-foreground))",
        },
        secondary: {
          DEFAULT: "hsl(var(--secondary))",
          foreground: "hsl(var(--secondary-foreground))",
        },
        destructive: {
          DEFAULT: "hsl(var(--destructive))",
          foreground: "hsl(var(--destructive-foreground))",
        },
        muted: {
          DEFAULT: "hsl(var(--muted))",
          foreground: "hsl(var(--muted-foreground))",
        },
        accent: {
          DEFAULT: "hsl(var(--accent))",
          foreground: "hsl(var(--accent-foreground))",
        },
        success: {
          DEFAULT: "hsl(var(--success))",
          foreground: "hsl(var(--success-foreground))",
        },
        warning: {
          DEFAULT: "hsl(var(--warning))",
          foreground: "hsl(var(--warning-foreground))",
        },
        popover: {
          DEFAULT: "hsl(var(--popover))",
          foreground: "hsl(var(--popover-foreground))",
        },
        card: {
          DEFAULT: "hsl(var(--card))",
          foreground: "hsl(var(--card-foreground))",
        },
        sidebar: {
          DEFAULT: "hsl(var(--sidebar-background))",
          foreground: "hsl(var(--sidebar-foreground))",
          primary: "hsl(var(--sidebar-primary))",
          "primary-foreground": "hsl(var(--sidebar-primary-foreground))",
          accent: "hsl(var(--sidebar-accent))",
          "accent-foreground": "hsl(var(--sidebar-accent-foreground))",
          border: "hsl(var(--sidebar-border))",
          ring: "hsl(var(--sidebar-ring))",
        },
        badge: {
          newbie: "hsl(var(--badge-newbie))",
          explorer: "hsl(var(--badge-explorer))",
          achiever: "hsl(var(--badge-achiever))",
          specialist: "hsl(var(--badge-specialist))",
          expert: "hsl(var(--badge-expert))",
          master: "hsl(var(--badge-master))",
        },
      },
      borderRadius: {
        lg: "var(--radius)",
        md: "calc(var(--radius) - 2px)",
        sm: "calc(var(--radius) - 4px)",
      },
      keyframes: {
        "accordion-down": {
          from: { height: "0" },
          to: { height: "var(--radix-accordion-content-height)" },
        },
        "accordion-up": {
          from: { height: "var(--radix-accordion-content-height)" },
          to: { height: "0" },
        },
        "fade-in": {
          from: { opacity: "0", transform: "translateY(10px)" },
          to: { opacity: "1", transform: "translateY(0)" },
        },
        "fade-in-up": {
          from: { opacity: "0", transform: "translateY(20px)" },
          to: { opacity: "1", transform: "translateY(0)" },
        },
        "scale-in": {
          from: { opacity: "0", transform: "scale(0.95)" },
          to: { opacity: "1", transform: "scale(1)" },
        },
        "slide-in-right": {
          from: { transform: "translateX(100%)" },
          to: { transform: "translateX(0)" },
        },
        "progress-fill": {
          from: { width: "0%" },
          to: { width: "var(--progress-value)" },
        },
        shimmer: {
          "100%": { transform: "translateX(100%)" },
        },
        pulse: {
          "0%, 100%": { opacity: "1" },
          "50%": { opacity: "0.5" },
        },
        "points-pop": {
          "0%": { transform: "scale(0)", opacity: "0" },
          "50%": { transform: "scale(1.2)" },
          "100%": { transform: "scale(1)", opacity: "1" },
        },
      },
      animation: {
        "accordion-down": "accordion-down 0.2s ease-out",
        "accordion-up": "accordion-up 0.2s ease-out",
        "fade-in": "fade-in 0.3s ease-out",
        "fade-in-up": "fade-in-up 0.4s ease-out",
        "scale-in": "scale-in 0.2s ease-out",
        "slide-in-right": "slide-in-right 0.3s ease-out",
        "progress-fill": "progress-fill 1s ease-out forwards",
        shimmer: "shimmer 2s infinite",
        pulse: "pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite",
        "points-pop": "points-pop 0.5s ease-out forwards",
      },
      fontFamily: {
        sans: ["Roboto", "system-ui", "sans-serif"],
        display: ["Roboto", "system-ui", "sans-serif"],
      },
    },
  },
  plugins: [require("tailwindcss-animate")],
} satisfies Config;
